contracts/BlockscoutIntegration.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title BlockscoutIntegration
 * @dev ETH Prague 2025 - Blockscout Integration ($20k Prize Pool)
 * @notice Advanced blockchain explorer integration with API endpoints
 */
contract BlockscoutIntegration {
    
    struct TransactionRecord {
        address user;
        bytes32 txHash;
        uint256 timestamp;
        string actionType;
        uint256 blockNumber;
        bool verified;
    }
    
    struct ContractMetadata {
        address contractAddress;
        string name;
        string version;
        bool isVerified;
        uint256 verificationTimestamp;
        string sourceCodeHash;
    }
    
    mapping(address => TransactionRecord[]) public userTransactions;
    mapping(address => ContractMetadata) public contractRegistry;
    mapping(address => uint256) public userMerits; // Blockscout Merits integration
    
    address public admin;
    string public blockscoutApiEndpoint;
    
    event TransactionTracked(address indexed user, bytes32 indexed txHash, string actionType);
    event ContractVerified(address indexed contractAddr, string name, string version);
    event MeritsAwarded(address indexed user, uint256 amount, string reason);
    event BlockscoutApiUpdated(string newEndpoint);
    
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can call this function");
        _;
    }
    
    constructor(string memory _apiEndpoint) {
        admin = msg.sender;
        blockscoutApiEndpoint = _apiEndpoint;
    }
    
    /**
     * @dev Track user transaction for Blockscout analytics
     * @param user User address
     * @param txHash Transaction hash
     * @param actionType Type of action performed
     */
    function trackTransaction(
        address user,
        bytes32 txHash,
        string calldata actionType
    ) external {
        TransactionRecord memory record = TransactionRecord({
            user: user,
            txHash: txHash,
            timestamp: block.timestamp,
            actionType: actionType,
            blockNumber: block.number,
            verified: true
        });
        
        userTransactions[user].push(record);
        
        // Award Merits for activity
        _awardMerits(user, 10, "Transaction Activity");
        
        emit TransactionTracked(user, txHash, actionType);
    }
    
    /**
     * @dev Register verified contract in Blockscout
     * @param contractAddr Contract address
     * @param name Contract name
     * @param version Contract version
     * @param sourceCodeHash Hash of source code
     */
    function registerVerifiedContract(
        address contractAddr,
        string calldata name,
        string calldata version,
        string calldata sourceCodeHash
    ) external onlyAdmin {
        contractRegistry[contractAddr] = ContractMetadata({
            contractAddress: contractAddr,
            name: name,
            version: version,
            isVerified: true,
            verificationTimestamp: block.timestamp,
            sourceCodeHash: sourceCodeHash
        });
        
        emit ContractVerified(contractAddr, name, version);
    }
    
    /**
     * @dev Award Merits to user (Blockscout points system)
     * @param user User address
     * @param amount Merit amount
     * @param reason Reason for merit award
     */
    function _awardMerits(address user, uint256 amount, string memory reason) internal {
        userMerits[user] += amount;
        emit MeritsAwarded(user, amount, reason);
    }
    
    /**
     * @dev Get user's transaction history
     * @param user User address
     * @return Array of transaction records
     */
    function getUserTransactions(address user) external view returns (TransactionRecord[] memory) {
        return userTransactions[user];
    }
    
    /**
     * @dev Get contract verification status
     * @param contractAddr Contract address
     * @return Contract metadata
     */
    function getContractInfo(address contractAddr) external view returns (ContractMetadata memory) {
        return contractRegistry[contractAddr];
    }
    
    /**
     * @dev Update Blockscout API endpoint
     * @param newEndpoint New API endpoint URL
     */
    function updateApiEndpoint(string calldata newEndpoint) external onlyAdmin {
        blockscoutApiEndpoint = newEndpoint;
        emit BlockscoutApiUpdated(newEndpoint);
    }
    
    /**
     * @dev Get user's total merits
     * @param user User address
     * @return Total merits earned
     */
    function getUserMerits(address user) external view returns (uint256) {
        return userMerits[user];
    }
}

contracts/BlockscoutRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title BlockscoutRegistry
 * @dev Blockchain transparency contract for ETH Prague 2025 ($20k Prize Pool)
 * Integrates with Blockscout for enhanced transaction tracking and transparency
 */
contract BlockscoutRegistry {
    
    struct TransactionRecord {
        bytes32 txHash;
        address from;
        address to;
        uint256 value;
        bytes data;
        uint256 blockNumber;
        uint256 timestamp;
        string category; // "proposal", "vote", "kyc", "token-transfer"
        string description;
        bool isVerified;
    }
    
    struct ContractMetadata {
        string name;
        string description;
        string version;
        address contractAddress;
        uint256 deployBlock;
        string sourceCodeUrl;
        bool isVerified;
        string[] tags;
    }
    
    struct UserActivity {
        uint256 totalTransactions;
        uint256 totalProposals;
        uint256 totalVotes;
        uint256 lastActivityBlock;
        uint256 reputationScore;
        bool isActive;
    }
    
    mapping(bytes32 => TransactionRecord) public transactionRecords;
    mapping(address => ContractMetadata) public contractRegistry;
    mapping(address => UserActivity) public userActivities;
    mapping(string => bytes32[]) public transactionsByCategory;
    mapping(address => bytes32[]) public userTransactions;
    
    address public admin;
    address public blockscoutOperator;
    uint256 public totalRecords;
    
    // Events for Blockscout indexing
    event TransactionRegistered(
        bytes32 indexed txHash,
        address indexed from,
        address indexed to,
        string category,
        uint256 timestamp
    );
    
    event ContractRegistered(
        address indexed contractAddress,
        string name,
        string version,
        uint256 deployBlock
    );
    
    event UserActivityUpdated(
        address indexed user,
        uint256 totalTransactions,
        uint256 reputationScore
    );
    
    event MetadataUpdated(bytes32 indexed txHash, string description);
    
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can call this function");
        _;
    }
    
    modifier onlyAuthorized() {
        require(
            msg.sender == admin || msg.sender == blockscoutOperator,
            "Only authorized addresses can call this function"
        );
        _;
    }
    
    constructor(address _blockscoutOperator) {
        admin = msg.sender;
        blockscoutOperator = _blockscoutOperator;
        totalRecords = 0;
        
        // Register this contract itself
        _registerContract(
            address(this),
            "BlockscoutRegistry",
            "Transparency registry for GTU DAO",
            "1.0.0",
            new string[](0)
        );
    }
    
    /**
     * @dev Register a transaction for enhanced tracking
     * @param txHash Transaction hash
     * @param from Sender address
     * @param to Recipient address
     * @param value Transaction value
     * @param data Transaction data
     * @param category Transaction category
     * @param description Human-readable description
     */
    function registerTransaction(
        bytes32 txHash,
        address from,
        address to,
        uint256 value,
        bytes memory data,
        string memory category,
        string memory description
    ) external onlyAuthorized {
        
        require(transactionRecords[txHash].timestamp == 0, "Transaction already registered");
        
        transactionRecords[txHash] = TransactionRecord({
            txHash: txHash,
            from: from,
            to: to,
            value: value,
            data: data,
            blockNumber: block.number,
            timestamp: block.timestamp,
            category: category,
            description: description,
            isVerified: true
        });
        
        // Update user activity
        _updateUserActivity(from, category);
        if (from != to) {
            _updateUserActivity(to, category);
        }
        
        // Add to category mapping
        transactionsByCategory[category].push(txHash);
        
        // Add to user transaction history
        userTransactions[from].push(txHash);
        if (from != to) {
            userTransactions[to].push(txHash);
        }
        
        totalRecords++;
        
        emit TransactionRegistered(txHash, from, to, category, block.timestamp);
    }
    
    /**
     * @dev Register a smart contract for tracking
     * @param contractAddress Address of the contract
     * @param name Contract name
     * @param description Contract description
     * @param version Contract version
     * @param tags Array of tags for categorization
     */
    function registerContract(
        address contractAddress,
        string memory name,
        string memory description,
        string memory version,
        string[] memory tags
    ) external onlyAdmin {
        _registerContract(contractAddress, name, description, version, tags);
    }
    
    /**
     * @dev Internal function to register contract
     */
    function _registerContract(
        address contractAddress,
        string memory name,
        string memory description,
        string memory version,
        string[] memory tags
    ) internal {
        require(contractRegistry[contractAddress].deployBlock == 0, "Contract already registered");
        
        contractRegistry[contractAddress] = ContractMetadata({
            name: name,
            description: description,
            version: version,
            contractAddress: contractAddress,
            deployBlock: block.number,
            sourceCodeUrl: "",
            isVerified: false,
            tags: tags
        });
        
        emit ContractRegistered(contractAddress, name, version, block.number);
    }
    
    /**
     * @dev Update user activity metrics
     * @param user User address
     * @param category Activity category
     */
    function _updateUserActivity(address user, string memory category) internal {
        UserActivity storage activity = userActivities[user];
        
        activity.totalTransactions++;
        activity.lastActivityBlock = block.number;
        activity.isActive = true;
        
        // Update specific counters based on category
        if (keccak256(bytes(category)) == keccak256(bytes("proposal"))) {
            activity.totalProposals++;
            activity.reputationScore += 10;
        } else if (keccak256(bytes(category)) == keccak256(bytes("vote"))) {
            activity.totalVotes++;
            activity.reputationScore += 5;
        } else if (keccak256(bytes(category)) == keccak256(bytes("kyc"))) {
            activity.reputationScore += 20;
        }
        
        emit UserActivityUpdated(user, activity.totalTransactions, activity.reputationScore);
    }
    
    /**
     * @dev Get transaction details
     * @param txHash Transaction hash
     * @return record TransactionRecord struct
     */
    function getTransaction(bytes32 txHash) external view returns (TransactionRecord memory) {
        require(transactionRecords[txHash].timestamp > 0, "Transaction not found");
        return transactionRecords[txHash];
    }
    
    /**
     * @dev Get transactions by category
     * @param category Transaction category
     * @return txHashes Array of transaction hashes
     */
    function getTransactionsByCategory(string memory category) external view returns (bytes32[] memory) {
        return transactionsByCategory[category];
    }
    
    /**
     * @dev Get user transaction history
     * @param user User address
     * @return txHashes Array of transaction hashes
     */
    function getUserTransactions(address user) external view returns (bytes32[] memory) {
        return userTransactions[user];
    }
    
    /**
     * @dev Get user activity summary
     * @param user User address
     * @return activity UserActivity struct
     */
    function getUserActivity(address user) external view returns (UserActivity memory) {
        return userActivities[user];
    }
    
    /**
     * @dev Get contract metadata
     * @param contractAddress Contract address
     * @return metadata ContractMetadata struct
     */
    function getContractMetadata(address contractAddress) external view returns (ContractMetadata memory) {
        return contractRegistry[contractAddress];
    }
    
    /**
     * @dev Update transaction metadata
     * @param txHash Transaction hash
     * @param description New description
     */
    function updateTransactionMetadata(
        bytes32 txHash,
        string memory description
    ) external onlyAuthorized {
        require(transactionRecords[txHash].timestamp > 0, "Transaction not found");
        
        transactionRecords[txHash].description = description;
        emit MetadataUpdated(txHash, description);
    }
    
    /**
     * @dev Verify contract source code
     * @param contractAddress Contract address
     * @param sourceCodeUrl URL to source code
     */
    function verifyContract(
        address contractAddress,
        string memory sourceCodeUrl
    ) external onlyAdmin {
        require(contractRegistry[contractAddress].deployBlock > 0, "Contract not registered");
        
        contractRegistry[contractAddress].sourceCodeUrl = sourceCodeUrl;
        contractRegistry[contractAddress].isVerified = true;
    }
    
    /**
     * @dev Get analytics data for Blockscout integration
     * @return totalTxs Total number of tracked transactions
     * @return totalContracts Total number of registered contracts
     * @return activeUsers Number of active users
     */
    function getAnalytics() external view returns (
        uint256 totalTxs,
        uint256 totalContracts,
        uint256 activeUsers
    ) {
        // Implementation would count actual data
        return (totalRecords, 0, 0); // Placeholder
    }
    
    /**
     * @dev Generate Blockscout-compatible metadata
     * @param txHash Transaction hash
     * @return metadata JSON-like string for Blockscout
     */
    function generateBlockscoutMetadata(bytes32 txHash) external view returns (string memory) {
        TransactionRecord memory record = transactionRecords[txHash];
        require(record.timestamp > 0, "Transaction not found");
        
        // Return structured metadata for Blockscout indexing
        return string(abi.encodePacked(
            '{"category":"', record.category, 
            '","description":"', record.description,
            '","timestamp":', _toString(record.timestamp),
            ',"verified":', record.isVerified ? "true" : "false",
            '}'
        ));
    }
    
    /**
     * @dev Update Blockscout operator
     * @param newOperator New operator address
     */
    function updateBlockscoutOperator(address newOperator) external onlyAdmin {
        blockscoutOperator = newOperator;
    }
    
    /**
     * @dev Utility function to convert uint to string
     */
    function _toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
    
    /**
     * @dev Emergency pause functions
     */
    function pause() external onlyAdmin {
        // Implementation for emergency pause
    }
    
    function unpause() external onlyAdmin {
        // Implementation for emergency unpause
    }
}

contracts/GTUDAOGovernance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./StatToken.sol";
import "./VotingWithWeight.sol";
import "./WorldIDVerifier.sol";
import "./VlayerZKVerifier.sol";
import "./BlockscoutRegistry.sol";

/**
 * @title GTUDAOGovernance
 * @dev Main governance contract for GTU DAO - ETH Prague 2025 ($40k Total Prize Pool)
 * Integrates all ETH Prague prize pools:
 * - World App ($10k): Identity verification with World ID
 * - vlayer ($10k): Zero-knowledge proofs for privacy
 * - Blockscout ($20k): Enhanced transparency and tracking
 */
contract GTUDAOGovernance {
    
    // Core contracts
    StatToken public statToken;
    VotingWithWeight public voting;
    WorldIDVerifier public worldVerifier;
    VlayerZKVerifier public vlayerVerifier;
    BlockscoutRegistry public blockscoutRegistry;
    
    // Governance settings
    address public admin;
    uint256 public constant MIN_STAT_FOR_PROPOSAL = 100;
    uint256 public constant MIN_STAT_FOR_VOTING = 10;
    uint256 public constant PROPOSAL_DURATION = 7 days;
    
    // Member management
    enum MemberRole { MEMBER, CORE_TEAM, VICE_PRESIDENT, PRESIDENT }
    
    struct Member {
        address wallet;
        string name;
        string email;
        MemberRole role;
        uint256 joinDate;
        bool isVerifiedWithWorldID;
        bool hasZKProof;
        bool isActive;
        string studentId;
        string department;
    }
    
    mapping(address => Member) public members;
    mapping(address => bool) public isMember;
    address[] public memberAddresses;
    
    // Events
    event MemberAdded(
        address indexed memberAddress,
        string name,
        MemberRole role,
        uint256 timestamp
    );
    
    event MemberVerified(
        address indexed memberAddress,
        string verificationType,
        uint256 timestamp
    );
    
    event ProposalCreatedWithVerification(
        uint256 indexed proposalId,
        address indexed proposer,
        bool worldIDVerified,
        bool zkProofVerified,
        uint256 timestamp
    );
    
    event VoteCastWithPrivacy(
        uint256 indexed proposalId,
        bool isAnonymous,
        bytes32 zkProofId,
        uint256 timestamp
    );
    
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can call this function");
        _;
    }
    
    modifier onlyMember() {
        require(isMember[msg.sender], "Only members can call this function");
        _;
    }
    
    modifier onlyVerifiedMember() {
        require(isMember[msg.sender], "Not a member");
        require(
            members[msg.sender].isVerifiedWithWorldID || members[msg.sender].hasZKProof,
            "Member not verified"
        );
        _;
    }
    
    constructor() {
        admin = msg.sender;
        
        // Deploy core contracts
        statToken = new StatToken(address(this));
        voting = new VotingWithWeight(address(statToken));
        worldVerifier = new WorldIDVerifier("gtu_dao_app", "member_verification");
        vlayerVerifier = new VlayerZKVerifier(address(0)); // Will be updated with actual vlayer verifier
        blockscoutRegistry = new BlockscoutRegistry(address(this));
        
        // Register contracts in Blockscout
        string[] memory tags = new string[](2);
        tags[0] = "governance";
        tags[1] = "dao";
        
        blockscoutRegistry.registerContract(
            address(this),
            "GTU DAO Governance",
            "Main governance contract for GTU DAO",
            "1.0.0",
            tags
        );
        
        // Initialize admin as first member
        _addMember(
            admin,
            "Admin",
            "admin@gtu.edu.tr",
            MemberRole.PRESIDENT,
            "ADMIN001",
            "Computer Engineering"
        );
    }
    
    /**
     * @dev Add a new member to the DAO
     * @param memberAddress Wallet address of the member
     * @param name Full name
     * @param email Email address
     * @param role Member role
     * @param studentId Student ID
     * @param department Department
     */
    function addMember(
        address memberAddress,
        string memory name,
        string memory email,
        MemberRole role,
        string memory studentId,
        string memory department
    ) external onlyAdmin {
        _addMember(memberAddress, name, email, role, studentId, department);
    }
    
    /**
     * @dev Internal function to add member
     */
    function _addMember(
        address memberAddress,
        string memory name,
        string memory email,
        MemberRole role,
        string memory studentId,
        string memory department
    ) internal {
        require(!isMember[memberAddress], "Already a member");
        
        members[memberAddress] = Member({
            wallet: memberAddress,
            name: name,
            email: email,
            role: role,
            joinDate: block.timestamp,
            isVerifiedWithWorldID: false,
            hasZKProof: false,
            isActive: true,
            studentId: studentId,
            department: department
        });
        
        isMember[memberAddress] = true;
        memberAddresses.push(memberAddress);
        
        // Initialize STAT tokens based on role
        statToken.initializeUser(memberAddress, _roleToString(role));
        
        // Register transaction in Blockscout
        blockscoutRegistry.registerTransaction(
            keccak256(abi.encodePacked(memberAddress, block.timestamp)),
            admin,
            memberAddress,
            0,
            "",
            "member-addition",
            string(abi.encodePacked("Added member: ", name))
        );
        
        emit MemberAdded(memberAddress, name, role, block.timestamp);
    }
    
    /**
     * @dev Verify member with World ID (ETH Prague $10k prize integration)
     * @param merkleRoot World ID Merkle root
     * @param nullifierHash Nullifier hash
     * @param proof ZK proof array
     * @param verificationLevel Verification level
     */
    function verifyWithWorldID(
        uint256 merkleRoot,
        uint256 nullifierHash,
        uint256[8] memory proof,
        string memory verificationLevel
    ) external onlyMember {
        
        // Verify with World ID contract
        worldVerifier.verifyWithWorldID(
            merkleRoot,
            nullifierHash,
            proof,
            verificationLevel
        );
        
        // Update member status
        members[msg.sender].isVerifiedWithWorldID = true;
        
        // Reward with STAT tokens
        statToken.mint(msg.sender, 50, "World ID Verification");
        
        // Register in Blockscout
        blockscoutRegistry.registerTransaction(
            keccak256(abi.encodePacked(msg.sender, "worldid", block.timestamp)),
            msg.sender,
            address(worldVerifier),
            0,
            "",
            "kyc",
            "World ID verification completed"
        );
        
        emit MemberVerified(msg.sender, "World ID", block.timestamp);
    }
    
    /**
     * @dev Submit ZK proof for privacy verification (ETH Prague $10k vlayer integration)
     * @param proof ZK proof array
     * @param publicSignals Public signals
     * @param proofType Type of proof
     */
    function submitZKProof(
        uint256[8] memory proof,
        uint256[] memory publicSignals,
        string memory proofType
    ) external onlyMember returns (bytes32 proofId) {
        
        // Submit proof to vlayer verifier
        bytes32 nullifierHash = keccak256(abi.encodePacked(msg.sender, proofType, block.timestamp));
        proofId = vlayerVerifier.submitProof(proof, publicSignals, proofType, nullifierHash);
        
        // Update member status
        members[msg.sender].hasZKProof = true;
        
        // Reward with STAT tokens
        statToken.mint(msg.sender, 30, "ZK Proof Submission");
        
        // Register in Blockscout
        blockscoutRegistry.registerTransaction(
            keccak256(abi.encodePacked(msg.sender, "zkproof", block.timestamp)),
            msg.sender,
            address(vlayerVerifier),
            0,
            "",
            "kyc",
            string(abi.encodePacked("ZK proof submitted: ", proofType))
        );
        
        emit MemberVerified(msg.sender, "ZK Proof", block.timestamp);
        
        return proofId;
    }
    
    /**
     * @dev Create proposal with enhanced verification tracking
     * @param title Proposal title
     * @param description Proposal description
     * @param category Proposal category
     * @param proposalType Who can vote
     */
    function createProposal(
        string memory title,
        string memory description,
        string memory category,
        VotingWithWeight.ProposalType proposalType
    ) external onlyVerifiedMember returns (uint256 proposalId) {
        
        require(statToken.balanceOf(msg.sender) >= MIN_STAT_FOR_PROPOSAL, "Insufficient STAT");
        
        // Create proposal in voting contract
        voting.createProposal(title, description, category, proposalType, PROPOSAL_DURATION);
        proposalId = voting.getTotalProposals();
        
        // Check verification status
        bool worldIDVerified = members[msg.sender].isVerifiedWithWorldID;
        bool zkProofVerified = members[msg.sender].hasZKProof;
        
        // Register in Blockscout
        blockscoutRegistry.registerTransaction(
            keccak256(abi.encodePacked(msg.sender, "proposal", proposalId)),
            msg.sender,
            address(voting),
            0,
            "",
            "proposal",
            string(abi.encodePacked("Proposal created: ", title))
        );
        
        emit ProposalCreatedWithVerification(
            proposalId,
            msg.sender,
            worldIDVerified,
            zkProofVerified,
            block.timestamp
        );
        
        return proposalId;
    }
    
    /**
     * @dev Vote on proposal with optional anonymity
     * @param proposalId Proposal ID
     * @param support Vote choice
     * @param comment Vote comment
     * @param useAnonymousVoting Whether to use ZK proof for anonymity
     */
    function voteOnProposal(
        uint256 proposalId,
        bool support,
        string memory comment,
        bool useAnonymousVoting
    ) external onlyVerifiedMember {
        
        require(statToken.balanceOf(msg.sender) >= MIN_STAT_FOR_VOTING, "Insufficient STAT");
        
        bytes32 zkProofId = 0x0;
        
        if (useAnonymousVoting) {
            // Create anonymous voting proof
            uint256[] memory publicSignals = new uint256[](3);
            publicSignals[0] = support ? 1 : 0;
            publicSignals[1] = proposalId;
            publicSignals[2] = uint256(keccak256(abi.encodePacked(msg.sender, proposalId)));
            
            uint256[8] memory mockProof;
            for (uint i = 0; i < 8; i++) {
                mockProof[i] = uint256(keccak256(abi.encodePacked(msg.sender, i, block.timestamp)));
            }
            
            bytes32 nullifierHash = keccak256(abi.encodePacked(msg.sender, proposalId, "vote"));
            zkProofId = vlayerVerifier.submitProof(
                mockProof,
                publicSignals,
                "anonymous-voting",
                nullifierHash
            );
        }
        
        // Cast vote
        voting.vote(proposalId, support, comment);
        
        // Register in Blockscout
        blockscoutRegistry.registerTransaction(
            keccak256(abi.encodePacked(msg.sender, "vote", proposalId)),
            msg.sender,
            address(voting),
            0,
            "",
            "vote",
            useAnonymousVoting ? "Anonymous vote cast" : "Public vote cast"
        );
        
        emit VoteCastWithPrivacy(proposalId, useAnonymousVoting, zkProofId, block.timestamp);
    }
    
    /**
     * @dev Get member information
     * @param memberAddress Member address
     * @return member Member struct
     */
    function getMember(address memberAddress) external view returns (Member memory) {
        require(isMember[memberAddress], "Not a member");
        return members[memberAddress];
    }
    
    /**
     * @dev Get all member addresses
     * @return addresses Array of member addresses
     */
    function getAllMembers() external view returns (address[] memory) {
        return memberAddresses;
    }
    
    /**
     * @dev Get DAO statistics for ETH Prague demo
     * @return totalMembers DAO statistics
     */
    function getDAOStats() external view returns (
        uint256 totalMembers,
        uint256 worldIDVerified,
        uint256 zkProofVerified,
        uint256 totalProposals,
        uint256 totalVotes,
        uint256 totalSTATSupply
    ) {
        totalMembers = memberAddresses.length;
        totalProposals = voting.getTotalProposals();
        totalSTATSupply = statToken.totalSupply();
        
        // Count verified members
        for (uint i = 0; i < memberAddresses.length; i++) {
            if (members[memberAddresses[i]].isVerifiedWithWorldID) {
                worldIDVerified++;
            }
            if (members[memberAddresses[i]].hasZKProof) {
                zkProofVerified++;
            }
        }
        
        // totalVotes would need to be tracked separately for efficiency
        totalVotes = 0; // Placeholder
    }
    
    /**
     * @dev Convert role enum to string
     */
    function _roleToString(MemberRole role) internal pure returns (string memory) {
        if (role == MemberRole.PRESIDENT) return "PRESIDENT";
        if (role == MemberRole.VICE_PRESIDENT) return "VICE_PRESIDENT";
        if (role == MemberRole.CORE_TEAM) return "CORE_TEAM";
        return "MEMBER";
    }
    
    /**
     * @dev Emergency functions
     */
    function pauseAll() external onlyAdmin {
        // Pause all contracts
    }
    
    function updateContracts(
        address _statToken,
        address _voting,
        address _worldVerifier,
        address _vlayerVerifier,
        address _blockscoutRegistry
    ) external onlyAdmin {
        if (_statToken != address(0)) statToken = StatToken(_statToken);
        if (_voting != address(0)) voting = VotingWithWeight(_voting);
        if (_worldVerifier != address(0)) worldVerifier = WorldIDVerifier(_worldVerifier);
        if (_vlayerVerifier != address(0)) vlayerVerifier = VlayerZKVerifier(_vlayerVerifier);
        if (_blockscoutRegistry != address(0)) blockscoutRegistry = BlockscoutRegistry(_blockscoutRegistry);
    }
}

contracts/StatToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title StatToken
 * @dev Soulbound (non-transferable) token for GTU DAO governance weight
 * Used for reputation-based voting power in DAO decisions
 * 
 * Features:
 * - Non-transferable (soulbound behavior)
 * - Only DAO can mint/burn tokens
 * - Represents user status and contribution level
 * - Used as voting weight multiplier
 */
contract StatToken is ERC20, Ownable {
    
    // Events for tracking STAT changes
    event StatMinted(address indexed user, uint256 amount, string reason);
    event StatBurned(address indexed user, uint256 amount, string reason);
    event StatDecayed(address indexed user, uint256 amount);
    
    // User metadata for tracking contributions
    struct UserStats {
        uint256 lastActivityTimestamp;
        uint256 totalContributions;
        uint256 proposalsCreated;
        uint256 votesParticipated;
        string role;
    }
    
    mapping(address => UserStats) public userStats;
    
    // Constants for STAT management
    uint256 public constant DECAY_PERIOD = 30 days;
    uint256 public constant DECAY_RATE = 5; // 5% decay per period
    
    // Initial STAT amounts by role
    mapping(string => uint256) public roleInitialStats;
    
    constructor(address initialOwner) ERC20("GTU DAO Status Token", "STAT") Ownable(initialOwner) {
        // Set initial STAT amounts for different roles
        roleInitialStats["PRESIDENT"] = 1000;
        roleInitialStats["VICE_PRESIDENT"] = 800;
        roleInitialStats["CORE_TEAM"] = 600;
        roleInitialStats["MEMBER"] = 100;
    }
    
    /**
     * @dev Mint STAT tokens to a user with reason tracking
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     * @param reason Reason for minting (e.g., "Proposal Created", "Event Organized")
     */
    function mint(address to, uint256 amount, string memory reason) public onlyOwner {
        _mint(to, amount);
        userStats[to].lastActivityTimestamp = block.timestamp;
        userStats[to].totalContributions += amount;
        
        emit StatMinted(to, amount, reason);
    }
    
    /**
     * @dev Burn STAT tokens from a user with reason tracking
     * @param from Address to burn tokens from
     * @param amount Amount of tokens to burn
     * @param reason Reason for burning (e.g., "Spam Proposal", "Violation")
     */
    function burn(address from, uint256 amount, string memory reason) public onlyOwner {
        _burn(from, amount);
        
        emit StatBurned(from, amount, reason);
    }
    
    /**
     * @dev Initialize a new user with role-based STAT
     * @param user Address of the new user
     * @param role Role of the user (PRESIDENT, CORE_TEAM, etc.)
     */
    function initializeUser(address user, string memory role) public onlyOwner {
        uint256 initialAmount = roleInitialStats[role];
        require(initialAmount > 0, "Invalid role");
        
        _mint(user, initialAmount);
        userStats[user] = UserStats({
            lastActivityTimestamp: block.timestamp,
            totalContributions: initialAmount,
            proposalsCreated: 0,
            votesParticipated: 0,
            role: role
        });
        
        emit StatMinted(user, initialAmount, string(abi.encodePacked("Role: ", role)));
    }
    
    /**
     * @dev Update user activity for contribution tracking
     * @param user Address of the user
     * @param activityType Type of activity (proposal, vote, event)
     */
    function updateActivity(address user, string memory activityType) public onlyOwner {
        userStats[user].lastActivityTimestamp = block.timestamp;
        
        if (keccak256(bytes(activityType)) == keccak256(bytes("proposal"))) {
            userStats[user].proposalsCreated++;
        } else if (keccak256(bytes(activityType)) == keccak256(bytes("vote"))) {
            userStats[user].votesParticipated++;
        }
    }
    
    /**
     * @dev Apply decay to inactive users
     * @param user Address to apply decay to
     */
    function applyDecay(address user) public {
        require(
            block.timestamp >= userStats[user].lastActivityTimestamp + DECAY_PERIOD,
            "User is still active"
        );
        
        uint256 currentBalance = balanceOf(user);
        uint256 decayAmount = (currentBalance * DECAY_RATE) / 100;
        
        if (decayAmount > 0) {
            _burn(user, decayAmount);
            emit StatDecayed(user, decayAmount);
        }
    }
    
    /**
     * @dev Get user's complete stats
     * @param user Address to get stats for
     * @return stats UserStats struct
     */
    function getUserStats(address user) public view returns (UserStats memory) {
        return userStats[user];
    }
    
    /**
     * @dev Check if user needs decay
     * @param user Address to check
     * @return bool Whether user needs decay
     */
    function needsDecay(address user) public view returns (bool) {
        return block.timestamp >= userStats[user].lastActivityTimestamp + DECAY_PERIOD;
    }
    
    /**
     * @dev Override to prevent transfers (soulbound behavior)
     * Only minting (from 0x0) and burning (to 0x0) are allowed
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(
            from == address(0) || to == address(0), 
            "STAT is soulbound and cannot be transferred"
        );
        super._update(from, to, amount);
    }
    
    /**
     * @dev Disable approve function for soulbound token
     */
    function approve(address spender, uint256 amount) public pure override returns (bool) {
        revert("STAT tokens cannot be approved for transfer");
    }
    
    /**
     * @dev Disable transferFrom function for soulbound token
     */
    function transferFrom(address from, address to, uint256 amount) public pure override returns (bool) {
        revert("STAT tokens cannot be transferred");
    }
}

contracts/VlayerIntegration.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title VlayerIntegration  
 * @dev ETH Prague 2025 - vlayer ZK Integration ($10k Prize Pool)
 * @notice Advanced zero-knowledge proof verification and trustless data integration
 */
contract VlayerIntegration {
    
    struct WebProof {
        bytes32 proofHash;
        string dataSource;
        bytes32 dataHash;
        uint256 timestamp;
        address prover;
        bool verified;
        string metadata;
    }
    
    struct EmailProof {
        bytes32 proofHash;
        string emailDomain;
        bytes32 contentHash;
        address verifier;
        uint256 timestamp;
        bool isValid;
    }
    
    struct TimeTravelProof {
        bytes32 proofHash;
        uint256 blockNumber;
        bytes32 stateRoot;
        string dataType;
        address prover;
        uint256 timestamp;
        bool verified;
    }
    
    struct TeleportProof {
        bytes32 proofHash;
        uint256 sourceChainId;
        uint256 targetChainId;
        bytes32 crossChainHash;
        address relayer;
        uint256 timestamp;
        bool executed;
    }
    
    mapping(bytes32 => WebProof) public webProofs;
    mapping(bytes32 => EmailProof) public emailProofs;
    mapping(bytes32 => TimeTravelProof) public timeTravelProofs;
    mapping(bytes32 => TeleportProof) public teleportProofs;
    mapping(address => bytes32[]) public userProofs;
    
    address public admin;
    address public vlayerProver;
    uint256 public proofCounter;
    
    event WebProofVerified(bytes32 indexed proofHash, string dataSource, address prover);
    event EmailProofVerified(bytes32 indexed proofHash, string emailDomain, address verifier);
    event TimeTravelProofCreated(bytes32 indexed proofHash, uint256 blockNumber, address prover);
    event TeleportProofExecuted(bytes32 indexed proofHash, uint256 sourceChain, uint256 targetChain);
    event ProverUpdated(address indexed newProver);
    
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can call this function");
        _;
    }
    
    modifier onlyProver() {
        require(msg.sender == vlayerProver || msg.sender == admin, "Only prover can call this function");
        _;
    }
    
    constructor(address _vlayerProver) {
        admin = msg.sender;
        vlayerProver = _vlayerProver;
        proofCounter = 0;
    }
    
    /**
     * @dev Verify web proof from vlayer
     * @param proofHash Unique proof hash
     * @param dataSource URL or API endpoint
     * @param dataHash Hash of verified data
     * @param metadata Additional proof metadata
     */
    function verifyWebProof(
        bytes32 proofHash,
        string calldata dataSource,
        bytes32 dataHash,
        string calldata metadata
    ) external onlyProver {
        require(webProofs[proofHash].proofHash == bytes32(0), "Proof already exists");
        
        webProofs[proofHash] = WebProof({
            proofHash: proofHash,
            dataSource: dataSource,
            dataHash: dataHash,
            timestamp: block.timestamp,
            prover: msg.sender,
            verified: true,
            metadata: metadata
        });
        
        userProofs[msg.sender].push(proofHash);
        proofCounter++;
        
        emit WebProofVerified(proofHash, dataSource, msg.sender);
    }
    
    /**
     * @dev Verify email proof from vlayer
     * @param proofHash Unique proof hash
     * @param emailDomain Email domain (e.g., gmail.com)
     * @param contentHash Hash of email content
     */
    function verifyEmailProof(
        bytes32 proofHash,
        string calldata emailDomain,
        bytes32 contentHash
    ) external onlyProver {
        require(emailProofs[proofHash].proofHash == bytes32(0), "Email proof already exists");
        
        emailProofs[proofHash] = EmailProof({
            proofHash: proofHash,
            emailDomain: emailDomain,
            contentHash: contentHash,
            verifier: msg.sender,
            timestamp: block.timestamp,
            isValid: true
        });
        
        userProofs[msg.sender].push(proofHash);
        
        emit EmailProofVerified(proofHash, emailDomain, msg.sender);
    }
    
    /**
     * @dev Create time travel proof for historical data
     * @param proofHash Unique proof hash
     * @param blockNumber Historical block number
     * @param stateRoot State root at that block
     * @param dataType Type of historical data
     */
    function createTimeTravelProof(
        bytes32 proofHash,
        uint256 blockNumber,
        bytes32 stateRoot,
        string calldata dataType
    ) external onlyProver {
        require(timeTravelProofs[proofHash].proofHash == bytes32(0), "Time travel proof already exists");
        require(blockNumber < block.number, "Cannot time travel to future");
        
        timeTravelProofs[proofHash] = TimeTravelProof({
            proofHash: proofHash,
            blockNumber: blockNumber,
            stateRoot: stateRoot,
            dataType: dataType,
            prover: msg.sender,
            timestamp: block.timestamp,
            verified: true
        });
        
        userProofs[msg.sender].push(proofHash);
        
        emit TimeTravelProofCreated(proofHash, blockNumber, msg.sender);
    }
    
    /**
     * @dev Execute teleport proof for cross-chain operations
     * @param proofHash Unique proof hash
     * @param sourceChainId Source blockchain ID
     * @param targetChainId Target blockchain ID
     * @param crossChainHash Cross-chain transaction hash
     */
    function executeTeleportProof(
        bytes32 proofHash,
        uint256 sourceChainId,
        uint256 targetChainId,
        bytes32 crossChainHash
    ) external onlyProver {
        require(teleportProofs[proofHash].proofHash == bytes32(0), "Teleport proof already exists");
        require(sourceChainId != targetChainId, "Source and target chains must be different");
        
        teleportProofs[proofHash] = TeleportProof({
            proofHash: proofHash,
            sourceChainId: sourceChainId,
            targetChainId: targetChainId,
            crossChainHash: crossChainHash,
            relayer: msg.sender,
            timestamp: block.timestamp,
            executed: true
        });
        
        userProofs[msg.sender].push(proofHash);
        
        emit TeleportProofExecuted(proofHash, sourceChainId, targetChainId);
    }
    
    /**
     * @dev Get web proof details
     * @param proofHash Proof hash
     * @return WebProof struct
     */
    function getWebProof(bytes32 proofHash) external view returns (WebProof memory) {
        return webProofs[proofHash];
    }
    
    /**
     * @dev Get email proof details
     * @param proofHash Proof hash
     * @return EmailProof struct
     */
    function getEmailProof(bytes32 proofHash) external view returns (EmailProof memory) {
        return emailProofs[proofHash];
    }
    
    /**
     * @dev Get time travel proof details
     * @param proofHash Proof hash
     * @return TimeTravelProof struct
     */
    function getTimeTravelProof(bytes32 proofHash) external view returns (TimeTravelProof memory) {
        return timeTravelProofs[proofHash];
    }
    
    /**
     * @dev Get teleport proof details
     * @param proofHash Proof hash
     * @return TeleportProof struct
     */
    function getTeleportProof(bytes32 proofHash) external view returns (TeleportProof memory) {
        return teleportProofs[proofHash];
    }
    
    /**
     * @dev Get user's proof history
     * @param user User address
     * @return Array of proof hashes
     */
    function getUserProofs(address user) external view returns (bytes32[] memory) {
        return userProofs[user];
    }
    
    /**
     * @dev Update vlayer prover address
     * @param newProver New prover address
     */
    function updateProver(address newProver) external onlyAdmin {
        vlayerProver = newProver;
        emit ProverUpdated(newProver);
    }
    
    /**
     * @dev Get total number of proofs
     * @return Total proof count
     */
    function getTotalProofs() external view returns (uint256) {
        return proofCounter;
    }
}

contracts/VlayerZKVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title VlayerZKVerifier
 * @dev Zero-Knowledge proof verification contract for ETH Prague 2025 ($10k Prize Pool)
 * Integrates with vlayer for privacy-preserving verification
 */
contract VlayerZKVerifier {
    
    struct ZKProof {
        uint256[8] proof;
        uint256[] publicSignals;
        string proofType; // "kyc-verification", "voting-eligibility", "anonymous-voting", "membership-verification"
        uint256 timestamp;
        bool isValid;
        address prover;
    }
    
    struct Circuit {
        string name;
        string description;
        bool isActive;
        uint256 maxPublicSignals;
    }
    
    mapping(bytes32 => ZKProof) public proofs;
    mapping(string => Circuit) public circuits;
    mapping(address => bytes32[]) public userProofs;
    mapping(bytes32 => bool) public nullifierHashes;
    
    address public admin;
    address public vlayerVerifier; // vlayer verifier contract address
    uint256 public proofCounter;
    
    // Events
    event ProofSubmitted(
        bytes32 indexed proofId,
        address indexed prover,
        string proofType,
        uint256 timestamp
    );
    
    event ProofVerified(
        bytes32 indexed proofId,
        bool isValid,
        uint256 timestamp
    );
    
    event CircuitAdded(string name, string description);
    event CircuitUpdated(string name, bool isActive);
    
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can call this function");
        _;
    }
    
    modifier validCircuit(string memory proofType) {
        require(circuits[proofType].isActive, "Circuit not active");
        _;
    }
    
    constructor(address _vlayerVerifier) {
        admin = msg.sender;
        vlayerVerifier = _vlayerVerifier;
        proofCounter = 0;
        
        // Initialize default circuits for ETH Prague demo
        _addCircuit("kyc-verification", "KYC verification with World ID integration", true, 4);
        _addCircuit("voting-eligibility", "Verify voting eligibility without revealing identity", true, 5);
        _addCircuit("anonymous-voting", "Anonymous voting with nullifier", true, 6);
        _addCircuit("membership-verification", "Prove DAO membership without revealing details", true, 4);
    }
    
    /**
     * @dev Submit a zero-knowledge proof for verification
     * @param proof ZK proof array
     * @param publicSignals Public signals array
     * @param proofType Type of proof being submitted
     * @param nullifierHash Optional nullifier hash for anonymous proofs
     */
    function submitProof(
        uint256[8] memory proof,
        uint256[] memory publicSignals,
        string memory proofType,
        bytes32 nullifierHash
    ) external validCircuit(proofType) returns (bytes32 proofId) {
        
        require(publicSignals.length <= circuits[proofType].maxPublicSignals, "Too many public signals");
        
        // Check nullifier uniqueness for anonymous proofs
        if (keccak256(bytes(proofType)) == keccak256(bytes("anonymous-voting"))) {
            require(!nullifierHashes[nullifierHash], "Nullifier already used");
            nullifierHashes[nullifierHash] = true;
        }
        
        proofCounter++;
        proofId = keccak256(abi.encodePacked(msg.sender, proofCounter, block.timestamp));
        
        // Store proof
        proofs[proofId] = ZKProof({
            proof: proof,
            publicSignals: publicSignals,
            proofType: proofType,
            timestamp: block.timestamp,
            isValid: false, // Will be verified next
            prover: msg.sender
        });
        
        userProofs[msg.sender].push(proofId);
        
        emit ProofSubmitted(proofId, msg.sender, proofType, block.timestamp);
        
        // Verify proof immediately
        _verifyProof(proofId);
        
        return proofId;
    }
    
    /**
     * @dev Internal proof verification
     * @param proofId ID of the proof to verify
     */
    function _verifyProof(bytes32 proofId) internal {
        ZKProof storage zkProof = proofs[proofId];
        
        // Mock verification for demo - in production, integrate with vlayer verifier
        bool isValid = _mockVerifyWithVlayer(zkProof.proof, zkProof.publicSignals, zkProof.proofType);
        
        zkProof.isValid = isValid;
        
        emit ProofVerified(proofId, isValid, block.timestamp);
    }
    
    /**
     * @dev Mock vlayer verification (replace with actual vlayer integration)
     * @param proof ZK proof array
     * @param publicSignals Public signals
     * @param proofType Type of proof
     * @return bool Whether proof is valid
     */
    function _mockVerifyWithVlayer(
        uint256[8] memory proof,
        uint256[] memory publicSignals,
        string memory proofType
    ) internal pure returns (bool) {
        // Mock verification logic for demo
        // In production, this would call vlayer verifier contract
        
        if (proof[0] == 0 || publicSignals.length == 0) {
            return false;
        }
        
        // Different validation rules for different proof types
        if (keccak256(bytes(proofType)) == keccak256(bytes("kyc-verification"))) {
            return publicSignals.length >= 3; // address, level, timestamp
        } else if (keccak256(bytes(proofType)) == keccak256(bytes("voting-eligibility"))) {
            return publicSignals.length >= 4; // address, role, joinDate, proposalId
        } else if (keccak256(bytes(proofType)) == keccak256(bytes("anonymous-voting"))) {
            return publicSignals.length >= 3; // vote, proposalId, nullifier
        } else if (keccak256(bytes(proofType)) == keccak256(bytes("membership-verification"))) {
            return publicSignals.length >= 3; // commitment, role, timestamp
        }
        
        return true; // Default to valid for demo
    }
    
    /**
     * @dev Verify a submitted proof (external call)
     * @param proofId ID of the proof to verify
     * @return bool Whether proof is valid
     */
    function verifyProof(bytes32 proofId) external view returns (bool) {
        require(proofs[proofId].timestamp > 0, "Proof does not exist");
        return proofs[proofId].isValid;
    }
    
    /**
     * @dev Get proof details
     * @param proofId ID of the proof
     * @return proof ZKProof struct
     */
    function getProof(bytes32 proofId) external view returns (ZKProof memory) {
        require(proofs[proofId].timestamp > 0, "Proof does not exist");
        return proofs[proofId];
    }
    
    /**
     * @dev Get user's proof history
     * @param user Address of the user
     * @return proofIds Array of proof IDs
     */
    function getUserProofs(address user) external view returns (bytes32[] memory) {
        return userProofs[user];
    }
    
    /**
     * @dev Add a new circuit (admin only)
     * @param name Circuit name
     * @param description Circuit description
     * @param maxPublicSignals Maximum number of public signals
     */
    function addCircuit(
        string memory name,
        string memory description,
        uint256 maxPublicSignals
    ) external onlyAdmin {
        _addCircuit(name, description, true, maxPublicSignals);
    }
    
    /**
     * @dev Internal function to add circuit
     */
    function _addCircuit(
        string memory name,
        string memory description,
        bool isActive,
        uint256 maxPublicSignals
    ) internal {
        circuits[name] = Circuit({
            name: name,
            description: description,
            isActive: isActive,
            maxPublicSignals: maxPublicSignals
        });
        
        emit CircuitAdded(name, description);
    }
    
    /**
     * @dev Update circuit status (admin only)
     * @param name Circuit name
     * @param isActive Whether circuit is active
     */
    function updateCircuit(string memory name, bool isActive) external onlyAdmin {
        require(bytes(circuits[name].name).length > 0, "Circuit does not exist");
        circuits[name].isActive = isActive;
        
        emit CircuitUpdated(name, isActive);
    }
    
    /**
     * @dev Get circuit details
     * @param name Circuit name
     * @return circuit Circuit struct
     */
    function getCircuit(string memory name) external view returns (Circuit memory) {
        return circuits[name];
    }
    
    /**
     * @dev Update vlayer verifier address (admin only)
     * @param _vlayerVerifier New verifier address
     */
    function updateVlayerVerifier(address _vlayerVerifier) external onlyAdmin {
        vlayerVerifier = _vlayerVerifier;
    }
    
    /**
     * @dev Get total number of proofs
     * @return uint256 Total proof count
     */
    function getTotalProofs() external view returns (uint256) {
        return proofCounter;
    }
    
    /**
     * @dev Check if nullifier has been used (for anonymous voting)
     * @param nullifierHash Nullifier hash to check
     * @return bool Whether nullifier has been used
     */
    function isNullifierUsed(bytes32 nullifierHash) external view returns (bool) {
        return nullifierHashes[nullifierHash];
    }
    
    /**
     * @dev Emergency functions
     */
    function pause() external onlyAdmin {
        // Implementation for pausing contract
    }
    
    function unpause() external onlyAdmin {
        // Implementation for unpausing contract
    }
}

contracts/VotingWithWeight.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IStatToken {
    function balanceOf(address account) external view returns (uint256);
    function updateActivity(address user, string memory activityType) external;
    function mint(address to, uint256 amount, string memory reason) external;
}

/**
 * @title VotingWithWeight
 * @dev STAT token weighted voting system for GTU DAO
 * 
 * Features:
 * - Voting power based on STAT token balance
 * - Automatic STAT rewards for participation
 * - Support for different proposal types
 * - Time-limited voting periods
 * - Anti-double voting protection
 */
contract VotingWithWeight {
    
    IStatToken public statToken;
    address public admin;
    uint256 public proposalCounter;
    
    // Proposal categories and their required participation levels
    enum ProposalType { ALL_MEMBERS, CORE_TEAM, COUNCIL }
    enum ProposalStatus { ACTIVE, PASSED, REJECTED, EXPIRED }
    
    struct Proposal {
        uint256 id;
        string title;
        string description;
        string category;
        ProposalType proposalType;
        ProposalStatus status;
        address proposer;
        uint256 yesVotes;
        uint256 noVotes;
        uint256 totalVotingPower;
        uint256 deadline;
        uint256 createdAt;
        bool executed;
        mapping(address => bool) hasVoted;
        mapping(address => bool) voteChoice; // true = yes, false = no
    }
    
    mapping(uint256 => Proposal) public proposals;
    mapping(address => uint256[]) public userProposals;
    mapping(address => uint256[]) public userVotes;
    
    // Events
    event ProposalCreated(
        uint256 indexed proposalId,
        address indexed proposer,
        string title,
        ProposalType proposalType,
        uint256 deadline
    );
    
    event VoteCast(
        uint256 indexed proposalId,
        address indexed voter,
        bool support,
        uint256 weight,
        string comment
    );
    
    event ProposalExecuted(uint256 indexed proposalId, bool passed);
    
    // Constants for STAT rewards
    uint256 public constant PROPOSAL_CREATION_REWARD = 50;
    uint256 public constant VOTE_PARTICIPATION_REWARD = 10;
    uint256 public constant SUCCESSFUL_PROPOSAL_BONUS = 100;
    
    constructor(address _statToken) {
        statToken = IStatToken(_statToken);
        admin = msg.sender;
        proposalCounter = 0;
    }
    
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can call this function");
        _;
    }
    
    /**
     * @dev Create a new proposal
     * @param _title Title of the proposal
     * @param _description Detailed description
     * @param _category Category (EVENT, FINANCIAL, etc.)
     * @param _proposalType Who can vote on this proposal
     * @param _duration Voting duration in seconds
     */
    function createProposal(
        string memory _title,
        string memory _description,
        string memory _category,
        ProposalType _proposalType,
        uint256 _duration
    ) external {
        require(bytes(_title).length > 0, "Title cannot be empty");
        require(bytes(_description).length > 0, "Description cannot be empty");
        require(_duration > 0, "Duration must be positive");
        
        // Check if user has minimum STAT to create proposal
        uint256 userStat = statToken.balanceOf(msg.sender);
        require(userStat >= 50, "Insufficient STAT to create proposal");
        
        proposalCounter++;
        uint256 deadline = block.timestamp + _duration;
        
        Proposal storage newProposal = proposals[proposalCounter];
        newProposal.id = proposalCounter;
        newProposal.title = _title;
        newProposal.description = _description;
        newProposal.category = _category;
        newProposal.proposalType = _proposalType;
        newProposal.status = ProposalStatus.ACTIVE;
        newProposal.proposer = msg.sender;
        newProposal.yesVotes = 0;
        newProposal.noVotes = 0;
        newProposal.totalVotingPower = 0;
        newProposal.deadline = deadline;
        newProposal.createdAt = block.timestamp;
        newProposal.executed = false;
        
        userProposals[msg.sender].push(proposalCounter);
        
        // Reward proposer with STAT
        statToken.mint(msg.sender, PROPOSAL_CREATION_REWARD, "Proposal Created");
        statToken.updateActivity(msg.sender, "proposal");
        
        emit ProposalCreated(proposalCounter, msg.sender, _title, _proposalType, deadline);
    }
    
    /**
     * @dev Cast a vote on a proposal
     * @param proposalId ID of the proposal to vote on
     * @param support true for YES, false for NO
     * @param comment Optional comment for the vote
     */
    function vote(
        uint256 proposalId, 
        bool support, 
        string memory comment
    ) external {
        Proposal storage proposal = proposals[proposalId];
        
        require(proposalId <= proposalCounter && proposalId > 0, "Invalid proposal ID");
        require(proposal.status == ProposalStatus.ACTIVE, "Proposal not active");
        require(block.timestamp < proposal.deadline, "Voting period ended");
        require(!proposal.hasVoted[msg.sender], "Already voted");
        
        uint256 weight = statToken.balanceOf(msg.sender);
        require(weight > 0, "No STAT tokens, cannot vote");
        
        // Record the vote
        proposal.hasVoted[msg.sender] = true;
        proposal.voteChoice[msg.sender] = support;
        proposal.totalVotingPower += weight;
        
        if (support) {
            proposal.yesVotes += weight;
        } else {
            proposal.noVotes += weight;
        }
        
        userVotes[msg.sender].push(proposalId);
        
        // Reward voter with STAT
        statToken.mint(msg.sender, VOTE_PARTICIPATION_REWARD, "Vote Participation");
        statToken.updateActivity(msg.sender, "vote");
        
        emit VoteCast(proposalId, msg.sender, support, weight, comment);
    }
    
    /**
     * @dev Execute a proposal after voting period ends
     * @param proposalId ID of the proposal to execute
     */
    function executeProposal(uint256 proposalId) external {
        Proposal storage proposal = proposals[proposalId];
        
        require(proposalId <= proposalCounter && proposalId > 0, "Invalid proposal ID");
        require(proposal.status == ProposalStatus.ACTIVE, "Proposal not active");
        require(block.timestamp >= proposal.deadline, "Voting period not ended");
        require(!proposal.executed, "Proposal already executed");
        
        proposal.executed = true;
        
        // Determine result
        bool passed = proposal.yesVotes > proposal.noVotes;
        
        if (passed) {
            proposal.status = ProposalStatus.PASSED;
            // Bonus reward for successful proposal creator
            statToken.mint(
                proposal.proposer, 
                SUCCESSFUL_PROPOSAL_BONUS, 
                "Successful Proposal Bonus"
            );
        } else {
            proposal.status = ProposalStatus.REJECTED;
        }
        
        emit ProposalExecuted(proposalId, passed);
    }
    
    /**
     * @dev Get proposal details
     * @param proposalId ID of the proposal
     * @return id Basic proposal information
     */
    function getProposal(uint256 proposalId) external view returns (
        uint256 id,
        string memory title,
        string memory description,
        string memory category,
        ProposalType proposalType,
        ProposalStatus status,
        address proposer,
        uint256 yesVotes,
        uint256 noVotes,
        uint256 totalVotingPower,
        uint256 deadline,
        uint256 createdAt,
        bool executed
    ) {
        Proposal storage proposal = proposals[proposalId];
        return (
            proposal.id,
            proposal.title,
            proposal.description,
            proposal.category,
            proposal.proposalType,
            proposal.status,
            proposal.proposer,
            proposal.yesVotes,
            proposal.noVotes,
            proposal.totalVotingPower,
            proposal.deadline,
            proposal.createdAt,
            proposal.executed
        );
    }
    
    /**
     * @dev Check if user has voted on a proposal
     * @param proposalId ID of the proposal
     * @param user Address of the user
     * @return hasVoted Whether user has voted
     * @return voteChoice true if voted YES, false if voted NO
     */
    function getUserVote(uint256 proposalId, address user) external view returns (
        bool hasVoted,
        bool voteChoice
    ) {
        Proposal storage proposal = proposals[proposalId];
        return (proposal.hasVoted[user], proposal.voteChoice[user]);
    }
    
    /**
     * @dev Get voting results with percentages
     * @param proposalId ID of the proposal
     * @return result Voting result summary
     */
    function getVotingResult(uint256 proposalId) external view returns (string memory result) {
        Proposal storage proposal = proposals[proposalId];
        
        if (proposal.totalVotingPower == 0) {
            return "No votes cast";
        }
        
        uint256 yesPercentage = (proposal.yesVotes * 100) / proposal.totalVotingPower;
        uint256 noPercentage = (proposal.noVotes * 100) / proposal.totalVotingPower;
        
        if (proposal.yesVotes > proposal.noVotes) {
            return string(abi.encodePacked("PASSED (", toString(yesPercentage), "% YES)"));
        } else if (proposal.noVotes > proposal.yesVotes) {
            return string(abi.encodePacked("REJECTED (", toString(noPercentage), "% NO)"));
        } else {
            return "TIED";
        }
    }
    
    /**
     * @dev Get user's voting power
     * @param user Address of the user
     * @return Current STAT balance (voting power)
     */
    function getVotingPower(address user) external view returns (uint256) {
        return statToken.balanceOf(user);
    }
    
    /**
     * @dev Get total number of proposals
     * @return Total proposal count
     */
    function getTotalProposals() external view returns (uint256) {
        return proposalCounter;
    }
    
    /**
     * @dev Get user's proposal history
     * @param user Address of the user
     * @return Array of proposal IDs created by user
     */
    function getUserProposals(address user) external view returns (uint256[] memory) {
        return userProposals[user];
    }
    
    /**
     * @dev Get user's voting history
     * @param user Address of the user
     * @return Array of proposal IDs user has voted on
     */
    function getUserVotes(address user) external view returns (uint256[] memory) {
        return userVotes[user];
    }
    
    /**
     * @dev Utility function to convert uint to string
     * @param value Number to convert
     * @return String representation of the number
     */
    function toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
    
    /**
     * @dev Update admin address
     * @param newAdmin New admin address
     */
    function updateAdmin(address newAdmin) external onlyAdmin {
        require(newAdmin != address(0), "Invalid admin address");
        admin = newAdmin;
    }
}

